import type { Verse } from '../models/surah.model';

/** Placeholder verses when full text is not yet in the app. Replace with real verse data over time. */
function placeholderVerses(count: number): Verse[] {
  return Array.from({ length: count }, (_, i) => ({
    number: i + 1,
    arabic: '—',
    translation: `Verse ${i + 1}`
  }));
}

